package kz.kyrmyzyanik.userdetail.ui.loginimport androidx.appcompat.app.AppCompatActivityimport android.os.Bundleimport androidx.annotation.VisibleForTestingimport androidx.databinding.DataBindingUtilimport androidx.lifecycle.ViewModelProviderimport androidx.lifecycle.ViewModelProvidersimport kotlinx.android.synthetic.main.activity_login.*import kz.kyrmyzyanik.userdetail.Rimport kz.kyrmyzyanik.userdetail.databinding.ActivityLoginBindingimport kz.kyrmyzyanik.userdetail.di.Injectableimport kz.kyrmyzyanik.userdetail.ui.user.UsersActivityimport javax.inject.Injectclass LoginActivity : AppCompatActivity(), Injectable {    @Inject    @VisibleForTesting    lateinit var viewModelFactory: ViewModelProvider.Factory    private lateinit var dataBinding: ActivityLoginBinding    private lateinit var loginViewModel: LoginViewModel    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        dataBind()        setTitle("Login")        btn_login.setOnClickListener {            UsersActivity.start(this@LoginActivity)        }    }    private fun dataBind() {        loginViewModel = ViewModelProviders.of(this, viewModelFactory).get(LoginViewModel::class.java)        dataBinding = DataBindingUtil.setContentView(this, R.layout.activity_login)        dataBinding?.let {  it.viewModel = loginViewModel}    }}